import React, { useState, useMemo, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Box, IconButton, Typography, CircularProgress, Button } from '@mui/material';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import CourseComparison from './components/CourseComparison';
import Login from './components/Login';
import { supabase, checkIsAdmin } from './services/supabase';
import AdminTools from './components/AdminTools';
import SearchHistoryPage from './components/SearchHistoryPage';
import { generateOverlapExplanation, generateEmbedding } from './services/openai';
import { findSimilarCourses } from './services/similarity';
import { fetchStoredEmbeddings } from './services/supabase';

const MIN_INPUT_LENGTH = 10;
const MAX_INPUT_LENGTH = 5000;

const validateInput = (text) => {
    if (!text || typeof text !== 'string') return false;
    const cleaned = text.trim();
    return cleaned.length >= MIN_INPUT_LENGTH && cleaned.length <= MAX_INPUT_LENGTH;
};

const App = () => {
    // Initialize theme from localStorage or system preference
    const [mode, setMode] = useState(() => {
        const savedMode = localStorage.getItem('themeMode');
        if (savedMode) {
            return savedMode;
        }
        // Check system preference
        if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
            return 'dark';
        }
        return 'light';
    });

    // Listen for system theme changes
    useEffect(() => {
        const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');
        const handleChange = (e) => {
            if (!localStorage.getItem('themeMode')) {
                setMode(e.matches ? 'dark' : 'light');
            }
        };

        mediaQuery.addEventListener('change', handleChange);
        return () => mediaQuery.removeEventListener('change', handleChange);
    }, []);

    const theme = useMemo(
        () =>
            createTheme({
                palette: {
                    mode,
                    ...(mode === 'dark' ? {
                        background: {
                            default: '#121212',
                            paper: '#1e1e1e',
                        },
                        primary: {
                            main: '#90caf9',
                        },
                    } : {
                        background: {
                            default: '#f5f5f5',
                            paper: '#ffffff',
                        },
                    }),
                },
                components: {
                    MuiCssBaseline: {
                        styleOverrides: {
                            body: {
                                scrollbarColor: mode === 'dark' ? '#6b6b6b #2b2b2b' : '#959595 #f5f5f5',
                                '&::-webkit-scrollbar, & *::-webkit-scrollbar': {
                                    width: '8px',
                                    height: '8px',
                                },
                                '&::-webkit-scrollbar-thumb, & *::-webkit-scrollbar-thumb': {
                                    borderRadius: 8,
                                    backgroundColor: mode === 'dark' ? '#6b6b6b' : '#959595',
                                    border: '2px solid transparent',
                                },
                                '&::-webkit-scrollbar-track, & *::-webkit-scrollbar-track': {
                                    backgroundColor: mode === 'dark' ? '#2b2b2b' : '#f5f5f5',
                                    borderRadius: 8,
                                },
                            },
                        },
                    },
                },
            }),
        [mode],
    );

    const toggleColorMode = () => {
        const newMode = mode === 'light' ? 'dark' : 'light';
        setMode(newMode);
        localStorage.setItem('themeMode', newMode);
    };

    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [showAdmin, setShowAdmin] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);
    const [currentView, setCurrentView] = useState('main');
    const [searchHistory, setSearchHistory] = useState([]);
    const [results, setResults] = useState(null);
    const [loadingExplanations, setLoadingExplanations] = useState({});
    const [expandedExplanations, setExpandedExplanations] = useState({});
    const [error, setError] = useState(null);
    const [formData, setFormData] = useState({
        courseName: '',
        courseDescription: '',
        courseLiterature: ''
    });
    const [selectedSearchResult, setSelectedSearchResult] = useState(null);
    const [submitLoading, setSubmitLoading] = useState(false);

    // Definer fetchSearchHistory før vi bruker den
    const fetchSearchHistory = async () => {
        try {
            const { data, error } = await supabase
                .from('search_history')
                .select('*')
                .eq('user_id', user?.id)
                .order('created_at', { ascending: false })
                .limit(5);

            if (error) throw error;
            setSearchHistory(data || []);
        } catch (error) {
            console.error('Error fetching search history:', error);
        }
    };

    useEffect(() => {
        // Sjekk innlogget status ved oppstart
        const checkUser = async () => {
            try {
                const { data: { user } } = await supabase.auth.getUser();
                setUser(user);
                console.log('Logged in user:', user?.email);
            } catch (error) {
                console.error('Error checking auth status:', error);
            } finally {
                setLoading(false);
            }
        };

        checkUser();

        // Lytt på auth endringer
        const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
            setUser(session?.user ?? null);
        });

        return () => subscription.unsubscribe();
    }, []);

    useEffect(() => {
        const checkAdminStatus = async () => {
            if (user) {
                const adminStatus = await checkIsAdmin();
                console.log('Admin status:', adminStatus);
                setIsAdmin(adminStatus);
            }
        };
        
        checkAdminStatus();
    }, [user]);

    // Oppdater useEffect for å bruke den definerte funksjonen
    useEffect(() => {
        if (user) {
            fetchSearchHistory();
        }
    }, [user]);

    useEffect(() => {
        if (currentView === 'history' && user) {
            fetchSearchHistory();
        }
    }, [currentView, user]);

    const handleDeleteSearch = async (id) => {
        try {
            const { error } = await supabase
                .from('search_history')
                .delete()
                .match({ id });

            if (error) throw error;

            // Oppdater lokal søkehistorikk
            setSearchHistory(prev => prev.filter(item => item.id !== id));
        } catch (error) {
            console.error('Error deleting search:', error);
        }
    };

    const handleGenerateExplanation = async (course, courseText) => {
        setLoadingExplanations(prev => ({ ...prev, [course.kurskode]: true }));

        try {
            const explanation = await generateOverlapExplanation(
                {
                    name: course.kursnavn,
                    content: courseText,
                },
                course,
                course.similarity
            );

            if (!explanation) {
                throw new Error('No explanation was generated');
            }

            setResults(prevResults =>
                prevResults.map(r =>
                    r.kurskode === course.kurskode
                        ? { ...r, explanation }
                        : r
                )
            );

            setExpandedExplanations(prev => ({
                ...prev,
                [course.kurskode]: true
            }));
        } catch (err) {
            console.error('Error generating explanation:', err);
        } finally {
            setLoadingExplanations(prev => ({ ...prev, [course.kurskode]: false }));
        }
    };

    const handleRestoreSearch = (historyItem) => {
        // Konverter de lagrede resultatene til samme format som live-resultater
        const restoredResults = historyItem.results.map(result => ({
            ...result,
            explanation: null // Start uten forklaringer
        }));
        
        setResults(restoredResults);
        setCurrentView('main');
        setExpandedExplanations({});
        setLoadingExplanations({});
    };

    // Optimaliser searchData ved å bare lagre nødvendig informasjon fra resultatene
    const optimizeResults = (results) => {
        return results.map(course => ({
            kurskode: course.kurskode,
            kursnavn: course.kursnavn,
            similarity: course.similarity,
            credits: course.credits,
            level_of_study: course.level_of_study,
            link_nb: course.link_nb
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setSubmitLoading(true);
        setError(null);
        setLoadingExplanations({});

        try {
            // Validate inputs
            if (!validateInput(formData.courseName)) {
                throw new Error('Course name must be between 10 and 5000 characters');
            }
            if (!validateInput(formData.courseDescription)) {
                throw new Error('Course description must be between 10 and 5000 characters');
            }
            if (formData.courseLiterature && !validateInput(formData.courseLiterature)) {
                throw new Error('Course literature must be between 10 and 5000 characters if provided');
            }

            // Combine course information for embedding
            const courseText = `
                Course Name: ${formData.courseName.trim()}
                Course Description: ${formData.courseDescription.trim()}
                ${formData.courseLiterature ? `Course Literature: ${formData.courseLiterature.trim()}` : ''}
            `.trim();

            console.log('Starting course analysis with text length:', courseText.length);

            // Generate embeddings for both Norwegian and English versions
            const embeddings = await generateEmbedding(courseText);
            console.log('Generated embeddings:', {
                norwegianLength: embeddings.norwegian.length,
                englishLength: embeddings.english.length
            });

            // Fetch stored courses
            const storedCourses = await fetchStoredEmbeddings();
            if (!storedCourses || storedCourses.length === 0) {
                throw new Error('No stored courses found to compare against');
            }

            // Find similar courses using the Norwegian embedding
            const similarCourses = await findSimilarCourses(embeddings.norwegian, storedCourses);
            if (!similarCourses || similarCourses.length === 0) {
                throw new Error('No similar courses found');
            }

            // Lagre søket og resultatene i historikk
            const { data: userData } = await supabase.auth.getUser();
            console.log('Current user:', userData);

            if (!userData?.user?.id) {
                throw new Error('No user ID found');
            }

            // Optimaliser data før lagring
            const searchData = {
                user_id: userData.user.id,
                course_name: formData.courseName.trim(),
                course_description: formData.courseDescription.trim(),
                course_literature: formData.courseLiterature?.trim() || '',
                results: optimizeResults(similarCourses),
                created_at: new Date().toISOString()
            };

            console.log('Attempting to save search data:', searchData);

            // Legg til timeout og retry logikk
            const saveWithRetry = async (retries = 3) => {
                try {
                    const { data, error: saveError } = await supabase
                        .from('search_history')
                        .insert([searchData])
                        .select()
                        .timeout(10000); // 10 sekunder timeout

                    if (saveError) {
                        throw saveError;
                    }
                    return { data, error: null };
                } catch (error) {
                    if (retries > 0 && (error.message.includes('timeout') || error.code === '40P01')) {
                        console.log(`Retry attempt ${4 - retries} for saving search history`);
                        await new Promise(resolve => setTimeout(resolve, 1000));
                        return saveWithRetry(retries - 1);
                    }
                    return { data: null, error };
                }
            };

            const { data, error: saveError } = await saveWithRetry();

            if (saveError) {
                console.error('Error saving search history:', {
                    error: saveError,
                    errorMessage: saveError.message,
                    errorDetails: saveError.details,
                    errorHint: saveError.hint,
                    data: searchData
                });
                // Vis feilmelding til bruker men fortsett med å vise resultater
                setError(`Note: Failed to save search history: ${saveError.message}`);
            } else {
                console.log('Successfully saved search history:', data);
                await fetchSearchHistory();
            }

            // Set results without explanations
            setResults(similarCourses.map(course => ({
                ...course,
                explanation: null
            })));

        } catch (err) {
            console.error('Error in course analysis:', err);
            setError('Failed to analyze course overlap: ' + (err.message || 'Unknown error'));
            setResults(null);
        } finally {
            setSubmitLoading(false);
        }
    };

    const handleViewResults = (searchItem) => {
        setSelectedSearchResult(searchItem.id === selectedSearchResult?.id ? null : searchItem);
    };

    if (loading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                <CircularProgress />
            </Box>
        );
    }

    if (!user) {
        return (
            <ThemeProvider theme={theme}>
                <CssBaseline />
                <Login onLogin={setUser} />
            </ThemeProvider>
        );
    }

    console.log('Render state:', { isAdmin, showAdmin, userEmail: user?.email });

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <Box sx={{
                minHeight: '100vh',
                bgcolor: 'background.default',
                position: 'relative',
                pb: 4
            }}>
                <Box sx={{
                    position: 'sticky',
                    top: 0,
                    zIndex: 1100,
                    bgcolor: 'background.paper',
                    borderBottom: 1,
                    borderColor: 'divider',
                    px: 2,
                    py: 1,
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    mb: 2
                }}>
                    <Typography
                        variant="h6"
                        sx={{
                            fontWeight: 700,
                            background: theme => theme.palette.mode === 'dark'
                                ? 'linear-gradient(45deg, #90caf9, #42a5f5)'
                                : 'linear-gradient(45deg, #1976d2, #42a5f5)',
                            backgroundClip: 'text',
                            WebkitBackgroundClip: 'text',
                            WebkitTextFillColor: 'transparent',
                        }}
                        onClick={() => setCurrentView('main')}
                        style={{ cursor: 'pointer' }}
                    >
                        SWAAKON
                    </Typography>
                    <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
                        {isAdmin && (
                            <Button 
                                onClick={() => setCurrentView(currentView === 'admin' ? 'main' : 'admin')}
                                variant="outlined"
                                size="small"
                                color={currentView === 'admin' ? 'primary' : 'inherit'}
                            >
                                {currentView === 'admin' ? 'Tilbake til app' : 'Admin Panel'}
                            </Button>
                        )}
                        <Button 
                            onClick={() => {
                                setCurrentView(currentView === 'history' ? 'main' : 'history');
                                if (currentView !== 'history') {
                                    fetchSearchHistory();
                                }
                            }}
                            variant="outlined"
                            size="small"
                            color={currentView === 'history' ? 'primary' : 'inherit'}
                        >
                            {currentView === 'history' ? 'Tilbake til app' : 'Siste søk'}
                        </Button>
                        <Button 
                            onClick={() => supabase.auth.signOut()} 
                            variant="outlined"
                            size="small"
                        >
                            Logg ut
                        </Button>
                        <IconButton
                            onClick={toggleColorMode}
                            size="large"
                            sx={{
                                bgcolor: mode === 'dark' ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.05)',
                                '&:hover': {
                                    bgcolor: mode === 'dark' ? 'rgba(255, 255, 255, 0.2)' : 'rgba(0, 0, 0, 0.1)',
                                },
                                borderRadius: 2,
                                p: 1,
                            }}
                            aria-label={mode === 'dark' ? 'Switch to light mode' : 'Switch to dark mode'}
                        >
                            {mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
                        </IconButton>
                    </Box>
                </Box>
                <Box sx={{ p: 2 }}>
                    {currentView === 'main' ? (
                        <CourseComparison 
                            results={results}
                            setResults={setResults}
                            loading={loading}
                            error={error}
                            formData={formData}
                            setFormData={setFormData}
                            loadingExplanations={loadingExplanations}
                            expandedExplanations={expandedExplanations}
                            onGenerateExplanation={handleGenerateExplanation}
                            setExpandedExplanations={setExpandedExplanations}
                            onSubmit={handleSubmit}
                        />
                    ) : currentView === 'admin' ? (
                        <AdminTools />
                    ) : (
                        <SearchHistoryPage
                            history={searchHistory}
                            onRestoreSearch={handleRestoreSearch}
                            onDeleteSearch={handleDeleteSearch}
                            results={selectedSearchResult}
                            onViewResults={handleViewResults}
                            loadingExplanations={loadingExplanations}
                            expandedExplanations={expandedExplanations}
                            onGenerateExplanation={handleGenerateExplanation}
                            setExpandedExplanations={setExpandedExplanations}
                        />
                    )}
                </Box>
            </Box>
        </ThemeProvider>
    );
};

export default App;
